        public string example => "7 6 4 2 1\r\n1 2 7 8 9\r\n9 7 6 2 1\r\n1 3 2 4 5\r\n8 6 4 4 1\r\n1 3 6 7 9";

        private int[][] parseData(string input)
        {
            string[] lines = input.Split("\r\n");
            List<int[]> ints = new List<int[]>();


            foreach (string line in lines)
            {
                string[] numbs = line.Split(' ');
                ints.Add(numbs.Select(int.Parse).ToArray());

            }

            return ints.ToArray();
        }

        public string Run(bool UseInput = true)
        {
            int[][] data;

            if (UseInput)  
                  data = parseData(input); 
            else  
                  data = parseData(example); 


            return data.Count(x => {
                //Console.WriteLine();
                return validList(x) == true;
            }).ToString() ;
        }

        public string Run2(bool UseInput = true)
        {
            int[][] data;

            if (UseInput)
                data = parseData(input);
            else
                data = parseData(example);


            return data.Count(x => {

               

                for (int i = 0; i < x.Length; i++) {

                    List<int> current = new List<int>(x);
                    current.RemoveAt(i);
                    bool isValid = validList(x);
                    if (validList(current.ToArray())) return true;

                }


                return false;
            }).ToString();
        }

        private bool validList(int[] ints)
        {
            int dir = 0;

            for (int i = 0; i < ints.Length - 1; i++)
            {
                int div = ints[i] - ints[i+1];

                if (div == 0)
                    return false;
                
                if (dir == 0)
                {
                    if (div < 0) dir = -1;
                    else dir = 1;
                }

                if ((dir == 1 && div < 0) ||
                   (dir == -1 && div > 0)) return false;

                if(int.Abs(div) > 3) return false; 

            }


            return true;
        }
    }
